Highcharts.chart('container-highchart-1', {
    chart: {
        type: 'column'
    },
    title: {
        text: 'Kinh doanh Viễn thông'
    },
    xAxis: {
        categories: [
            'Ưu tiên 1',
            'Ưu tiên 2',
            'Ưu tiên 3'
        ],
        crosshair: true
    },
    yAxis: {
        title: {
            useHTML: true,
            text: 'Số lượng'
        }
    },
    tooltip: {
        headerFormat: '<span style="font-size:10px">{point.key}</span><table>',
        pointFormat: '<tr><td style="color:{series.color};padding:0">{series.name}: </td>' +
            '<td style="padding:0"><b>{point.y:.1f}</b></td></tr>',
        footerFormat: '</table>',
        shared: true,
        useHTML: true
    },
    plotOptions: {
        column: {
            pointPadding: 0.2,
            borderWidth: 0
        }
    },
    colors: ['#FF0000', '#ffaa31', '#8ac248'],
    series: [{
        name: 'Danger',
        data: [3, 2, 4]

    }, {
        name: 'Warning',
        data: [5, 6, 7]

    }, {
        name: 'Ongoing',
        data: [13, 16, 15]

    }]
});

